mapper.select:
SELECT * FROM ${tbl_board}
WHERE ${board.uno} = #{uno}
ORDER BY ${board.bno} DESC
LIMIT #{start}, 10

SELECT * FROM ${tbl_board}
WHERE
${board.uno} = #{uno}
OR 
(${board.uno} IN (
	SELECT ${allwaiser.allwaiseruno}
	FROM ${tbl_allwaiser}
	WHERE ${allwaiser.uno} = #{uno}) 
AND (${board.privacy_bounds} = 0 OR ${board.privacy_bounds} = 1))
OR
(${board.privacy_bounds} = 0)
ORDER BY ${board.bno} DESC
LIMIT #{start}, 10

SELECT * FROM ${tbl_board}
WHERE ${board.uno} IN (
	SELECT ${allwaiser.allwaiseruno}
	FROM ${tbl_allwaiser}
	WHERE ${allwaiser.uno} = #{uno})
AND (${board.privacy_bounds} = 0 OR ${board.privacy_bounds} = 1)
ORDER BY ${board.bno} DESC
LIMIT #{start}, 10

SELECT COUNT(*) AS COUNT FROM ${tbl_board}
WHERE ${board.uno} IN (
	SELECT ${allwaiser.allwaiseruno}
	FROM ${tbl_allwaiser}
	WHERE ${allwaiser.uno} = #{uno})
AND (${board.privacy_bounds} = 0 OR ${board.privacy_bounds} = 1)

SELECT * FROM ${tbl_board}
WHERE ${board.uno} = #{uno}
OR
${board.uno} IN (
	SELECT ${allwaiser.allwaiseruno}
	FROM ${tbl_allwaiser}
	WHERE ${allwaiser.uno} = #{uno})
ORDER BY ${board.bno} DESC
LIMIT #{start}, 10

SELECT * FROM ${tbl_board} WHERE

	${board.bno} IN (
		SELECT ${board.bno}
		FROM ${tbl_board}
		WHERE INSTR(${board.content}, #{keyword}) > 0)


	${board.bno} IN (
		SELECT ${board.bno}
		FROM ${tbl_board}
		WHERE INSTR(${board.userid}, #{keyword}) > 0)


	${board.bno} IN (
		SELECT ${board.bno}
		FROM ${tbl_board}
		WHERE INSTR(${board.hashtag}, #{keyword}) > 0)

AND
(${board.privacy_bounds} = 0)
ORDER BY ${board.bno} DESC
LIMIT #{start}, 10

mapper.update:
UPDATE ${tbl_board}
SET
${board.pf_photo} = (
	SELECT ${user.pf_photo} FROM ${tbl_user} WHERE ${user.uno} = #{uno})
WHERE ${board.uno} = #{uno}
