mapper.select:
SELECT * FROM ${tbl_board} 
WHERE ${board.bno} 
IN (SELECT ${favorite.bno} FROM ${tbl_favorite} WHERE ${favorite.uno} = #{uno}) 
AND (${board.privacy_bounds} = 0 OR ${board.privacy_bounds} = 1) 
ORDER BY ${board.bno} DESC 
LIMIT #{start}, 10

SELECT COUNT(*) FROM ${tbl_board} 
WHERE ${board.bno} 
IN (SELECT ${favorite.bno} FROM ${tbl_favorite} WHERE ${favorite.uno} = #{uno}) 
AND (${board.privacy_bounds} = 0 OR ${board.privacy_bounds} = 1)

SELECT EXISTS(
	SELECT * FROM ${tbl_favorite}
	WHERE ${favorite.uno} = #{uno}
	AND ${favorite.bno} = #{bno})
AS isEXIST
